import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  // Initially center the "No" button within its container
  const [noButtonStyle, setNoButtonStyle] = useState({
    position: 'absolute',
    left: 'calc(50% - 40px)',
    top: 'calc(50% - 20px)',
  });

  // When the user hovers over the "No" button, move it to a random position
  const moveButton = () => {
    if (containerRef.current) {
      const containerWidth = containerRef.current.offsetWidth;
      const containerHeight = containerRef.current.offsetHeight;
      // Assume the button is roughly 80px wide and 40px tall
      const maxLeft = containerWidth - 80;
      const maxTop = containerHeight - 40;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({
        position: 'absolute',
        left: `${newLeft}px`,
        top: `${newTop}px`,
      });
    }
  };

  return (
    <div
      ref={containerRef}
      id="landing-page"
      style={{
        position: 'relative',
        height: '100vh',
        background: 'linear-gradient(135deg, #ff9a9e, #fad0c4)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#fff',
        padding: '20px',
      }}
    >
      <h1 style={{ fontSize: '3rem', marginBottom: '2rem' }}>
        Will you be my Valentine?
      </h1>
      <div style={{ position: 'relative', width: '300px', height: '100px' }}>
        <button
          onClick={() => navigate('/invite')}
          style={{
            padding: '10px 20px',
            fontSize: '1.2rem',
            cursor: 'pointer',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#e63946',
            color: '#fff',
            boxShadow: '0px 4px 6px rgba(0,0,0,0.1)',
          }}
        >
          Yes
        </button>
        <button
          onMouseEnter={moveButton}
          onClick={() => alert("Haha, you can't click me!")}
          style={{
            ...noButtonStyle,
            padding: '10px 20px',
            fontSize: '1.2rem',
            cursor: 'pointer',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#457b9d',
            color: '#fff',
            boxShadow: '0px 4px 6px rgba(0,0,0,0.1)',
          }}
        >
          No
        </button>
      </div>
    </div>
  );
}

function InvitePage() {
  // Prepopulate a Google Calendar event for Thursday at 5pm (example date: 2025-02-13)
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Valentine%27s+Date&dates=20250213T170000/20250213T180000&details=Let%27s+meet+for+a+fun+date+this+Thursday+at+5pm!";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div
      style={{
        height: '100vh',
        background: 'linear-gradient(135deg, #ff9a9e, #fad0c4)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#fff',
        padding: '20px',
      }}
    >
      <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>
        You're Invited!
      </h1>
      <p style={{ fontSize: '1.5rem', textAlign: 'center', marginBottom: '2rem' }}>
        Let's celebrate Valentine's Day with a special date this Thursday at 5pm.
        I can't wait to see you!
      </p>
      <button
        onClick={handleAccept}
        style={{
          padding: '10px 20px',
          fontSize: '1.2rem',
          cursor: 'pointer',
          borderRadius: '5px',
          border: 'none',
          backgroundColor: '#e63946',
          color: '#fff',
          boxShadow: '0px 4px 6px rgba(0,0,0,0.1)',
        }}
      >
        Accept
      </button>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/invite" element={<InvitePage />} />
      </Routes>
    </Router>
  );
}

export default App;
