import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  // Use a ref on the card container (which is relative positioned) so that
  // the No button always moves within the visible bounds of the card.
  const cardRef = useRef(null);
  const [noButtonStyle, setNoButtonStyle] = useState({
    position: 'absolute',
    right: '20px',
    top: '0px',
  });
  const [noButtonMessage, setNoButtonMessage] = useState('');
  const noMessages = [
    "haha you can't catch me ðŸƒ",
    "Nope, not this time âœ‹",
    "Still no! ðŸš«",
    "Seriously? "
  ];
  // When the "No" button is clicked, display a message and move the button.
  const handleNoClick = () => {
    const randomIndex = Math.floor(Math.random() * noMessages.length);
    setNoButtonMessage(noMessages[randomIndex]);
    if (cardRef.current) {
      const containerWidth = cardRef.current.offsetWidth;
      const containerHeight = cardRef.current.offsetHeight;
      // Assume the button is roughly 120px x 45px
      const buttonWidth = 120;
      const buttonHeight = 45;
      const maxLeft = containerWidth - buttonWidth;
      const maxTop = containerHeight - buttonHeight;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({
        position: 'absolute',
        left: `${newLeft}px`,
        top: `${newTop}px`,
        transition: 'all 0.1s ease-in-out',
      });
    }
  };

  return (
    <div className="page-container">
      {/* Decorative hearts floating around */}
      <div className="decorations">
        <span className="heart" style={{ top: '5%', left: '10%' }}>â™¥</span>
        <span className="heart" style={{ top: '15%', right: '15%' }}>â™¥</span>
        <span className="heart" style={{ top: '30%', left: '5%' }}>â™¥</span>
        <span className="heart" style={{ top: '40%', right: '5%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '20%', left: '20%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '10%', right: '20%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '25%', left: '40%' }}>â™¥</span>
        <span className="heart" style={{ top: '50%', right: '35%' }}>â™¥</span>
      </div>

      <div className="card" ref={cardRef}>
        <h1 className="title">Solomiya, will you be my Valentine?</h1>
        <h2 className="subtitle">Don't be "Solo" â€“ let's make it a duo!</h2>
        <div className="button-container">
          <button className="btn yes-btn" onClick={() => navigate('/invite')}>
            Yes
          </button>
          <button
            className="btn no-btn"
            onClick={handleNoClick}
            style={noButtonStyle}
          >
            No
          </button>
        </div>
        {noButtonMessage && <div className="message">{noButtonMessage}</div>}
      </div>
    </div>
  );
}

function InvitePage() {
  // Prepopulate a Google Calendar event for Solo.
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Solo%27s+Special+Valentine+Date&dates=20250213T170000/20250213T180000&details=Hi+Solo!+Join+me+for+a+memorable+evening+this+Thursday+at+5+pm.+Let%27s+create+beautiful+moments+together.";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div className="page-container">
      {/* Decorative hearts on the invite page */}
      <div className="decorations">
        <span className="heart" style={{ top: '8%', left: '12%' }}>â™¥</span>
        <span className="heart" style={{ top: '20%', right: '18%' }}>â™¥</span>
        <span className="heart" style={{ top: '35%', left: '8%' }}>â™¥</span>
        <span className="heart" style={{ top: '45%', right: '10%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '22%', left: '18%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '12%', right: '18%' }}>â™¥</span>
        <span className="heart" style={{ bottom: '28%', left: '28%' }}>â™¥</span>
        <span className="heart" style={{ top: '55%', right: '28%' }}>â™¥</span>
      </div>

      <div className="card">
        <h1 className="title">You're Invited, Solo!</h1>
        <p className="description">
          I would be honored if you joined me for a special Valentineâ€™s evening this Thursday at 5 pm.
          Letâ€™s celebrate love with elegance and create beautiful memories together.
        </p>
        <button className="btn accept-btn" onClick={handleAccept}>
          Accept
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <>
      {/* Background song (ensure background.mp3 is in your public folder) */}
      <audio src="/Lou Val - Eternal Sunshine (Official Visualizer).mp3" autoPlay loop />
      <Router>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/invite" element={<InvitePage />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
