import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [noButtonStyle, setNoButtonStyle] = useState({});

  // When the user hovers over the "No" button, move it to a random position within the container.
  const moveButton = () => {
    if (containerRef.current) {
      const containerWidth = containerRef.current.offsetWidth;
      const containerHeight = containerRef.current.offsetHeight;
      // Estimate button size (width: 100px, height: 40px)
      const buttonWidth = 100;
      const buttonHeight = 40;
      const maxLeft = containerWidth - buttonWidth;
      const maxTop = containerHeight - buttonHeight;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({ left: newLeft, top: newTop, position: 'absolute', transition: 'all 0.3s ease-in-out' });
    }
  };

  return (
    <div className="page-container" ref={containerRef}>
      <div className="card">
        <h1 className="title">Solomiya, will you be my Valentine?</h1>
        <h2 className="subtitle">Don't be "Solo" â€“ let's make it a duo!</h2>
        <div className="button-container">
          <button className="btn yes-btn" onClick={() => navigate('/invite')}>
            Yes
          </button>
          <button
            className="btn no-btn"
            onMouseEnter={moveButton}
            onClick={() => alert("Haha, you can't catch me!")}
            style={noButtonStyle}
          >
            No
          </button>
        </div>
      </div>
    </div>
  );
}

function InvitePage() {
  // Prepopulate a Google Calendar event for a special date invitation for Solo.
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Solo%27s+Special+Valentine+Date&dates=20250213T170000/20250213T180000&details=Hi+Solo!+I+would+be+thrilled+if+you+joined+me+for+a+special+evening+this+Thursday+at+5+pm.+Let's+create+beautiful+memories+together!+RSVP+to+confirm.";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div className="page-container">
      <div className="card">
        <h1 className="title">You're Invited, Solo!</h1>
        <p className="description">
          I would be honored if you joined me for an elegant and memorable evening this Thursday at 5 pm.
          Let's create beautiful moments together.
        </p>
        <button className="btn accept-btn" onClick={handleAccept}>
          Accept
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/invite" element={<InvitePage />} />
      </Routes>
    </Router>
  );
}

export default App;
