import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  const cardRef = useRef(null);
  const [noButtonStyle, setNoButtonStyle] = useState({
    position: 'absolute',
    right: '20px',
    top: '0px',
  });
  const [noButtonMessage, setNoButtonMessage] = useState('');
  const noMessages = [
    "haha you can't catch me ü§£ü´µ",
    "nope, not this time ‚ùå",
    "still no! üòµ",
    "seriously? üíî"
  ];
  const handleNoClick = () => {
    const randomIndex = Math.floor(Math.random() * noMessages.length);
    setNoButtonMessage(noMessages[randomIndex]);
    if (cardRef.current) {
      const containerWidth = cardRef.current.offsetWidth;
      const containerHeight = cardRef.current.offsetHeight;
      const buttonWidth = 120;
      const buttonHeight = 45;
      const maxLeft = containerWidth - buttonWidth;
      const maxTop = containerHeight - buttonHeight;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({
        position: 'absolute',
        left: `${newLeft}px`,
        top: `${newTop}px`,
        transition: 'all 0.1s ease-in-out',
      });
    }
  };

  return (
    <div className="page-container">
      {/* Decorative elements */}
      <div className="decorations">
        {/* Existing Hearts */}
        <span className="heart" style={{ top: '5%', left: '10%' }}>‚ô•</span>
        <span className="heart" style={{ top: '15%', right: '15%' }}>‚ô•</span>
        <span className="heart" style={{ top: '30%', left: '5%' }}>‚ô•</span>
        <span className="heart" style={{ top: '40%', right: '5%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '20%', left: '20%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '10%', right: '20%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '25%', left: '40%' }}>‚ô•</span>
        <span className="heart" style={{ top: '50%', right: '35%' }}>‚ô•</span>
        {/* Extra Hearts */}
        <span className="heart" style={{ top: '65%', left: '60%' }}>‚ô•</span>
        <span className="heart" style={{ top: '10%', right: '30%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '5%', left: '10%' }}>‚ô•</span>

        {/* Existing Cupid Arrows */}
        <span className="arrow" style={{ top: '25%', left: '50%' }}>üèπ</span>
        <span className="arrow" style={{ top: '70%', right: '10%' }}>üèπ</span>
        {/* Extra Cupid Arrows */}
        <span className="arrow" style={{ top: '40%', left: '70%' }}>üèπ</span>
        <span className="arrow" style={{ bottom: '30%', right: '30%' }}>üèπ</span>

        {/* Existing Teddy Bear */}
        <span className="teddy" style={{ bottom: '15%', left: '30%' }}>üß∏</span>
        {/* Extra Teddy Bears */}
        <span className="teddy" style={{ top: '80%', right: '50%' }}>üß∏</span>
        <span className="teddy" style={{ bottom: '40%', left: '50%' }}>üß∏</span>
        <span className="teddy" style={{ bottom: '0%', left: '50%' }}>üß∏</span>

      </div>

      <div className="card" ref={cardRef}>
        <h1 className="title">Solomiya, will you be my Valentine?</h1>
        <h2 className="subtitle">Don't be "Solo" ‚Äì let's make it a duo!</h2>
        <div className="button-container">
          <button className="btn yes-btn" onClick={() => navigate('/invite')}>
            Yes üíñ
          </button>
          <button
            className="btn no-btn"
            onClick={handleNoClick}
            style={noButtonStyle}
          >
            No
          </button>
        </div>
        {noButtonMessage && <div className="message">{noButtonMessage}</div>}
      </div>
    </div>
  );
}

function InvitePage() {
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Solo%27s+Special+Valentine+Date&dates=20250213T170000/20250213T180000&details=Hi+Solo!+Join+me+for+a+memorable+evening+this+Thursday+at+5+pm.+Let%27s+create+beautiful+moments+together.";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div className="page-container">
      {/* Decorative elements */}
      <div className="decorations">
        {/* Existing Hearts */}
        <span className="heart" style={{ top: '8%', left: '12%' }}>‚ô•</span>
        <span className="heart" style={{ top: '20%', right: '18%' }}>‚ô•</span>
        <span className="heart" style={{ top: '35%', left: '8%' }}>‚ô•</span>
        <span className="heart" style={{ top: '45%', right: '10%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '22%', left: '18%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '12%', right: '18%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '28%', left: '28%' }}>‚ô•</span>
        <span className="heart" style={{ top: '55%', right: '28%' }}>‚ô•</span>
        {/* Extra Hearts */}
        <span className="heart" style={{ top: '65%', left: '70%' }}>‚ô•</span>
        <span className="heart" style={{ bottom: '15%', right: '25%' }}>‚ô•</span>

        {/* Existing Cupid Arrow */}
