import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

function LandingPage() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [noButtonStyle, setNoButtonStyle] = useState({
    position: 'absolute',
    right: '20px',
    top: '0px',
  });
  const [noButtonMessage, setNoButtonMessage] = useState('');

  // When the "No" button is clicked, update the message and move it to a new random position.
  const handleNoClick = () => {
    // Set a friendly message that appears on the page.
    setNoButtonMessage("You can't escape love, Solo!");

    // Move the button to a random location within the viewport.
    if (containerRef.current) {
      const containerWidth = containerRef.current.offsetWidth;
      const containerHeight = containerRef.current.offsetHeight;
      // Estimate button dimensions (width: 120px, height: 45px)
      const buttonWidth = 120;
      const buttonHeight = 45;
      const maxLeft = containerWidth - buttonWidth;
      const maxTop = containerHeight - buttonHeight;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({
        position: 'absolute',
        left: `${newLeft}px`,
        top: `${newTop}px`,
        transition: 'all 0.3s ease-in-out',
      });
    }
  };

  return (
    <div className="page-container" ref={containerRef}>
      {/* Decorative hearts in the background */}
      <div className="decorations">
        <span className="heart" style={{ top: '10%', left: '15%' }}>♥</span>
        <span className="heart" style={{ top: '20%', right: '20%' }}>♥</span>
        <span className="heart" style={{ bottom: '15%', left: '25%' }}>♥</span>
        <span className="heart" style={{ bottom: '10%', right: '15%' }}>♥</span>
      </div>

      <div className="card">
        <h1 className="title">Solomiya, will you be my Valentine?</h1>
        <h2 className="subtitle">Don't be "Solo" – let's make it a duo!</h2>
        <div className="button-container">
          <button className="btn yes-btn" onClick={() => navigate('/invite')}>
            Yes
          </button>
          <button
            className="btn no-btn"
            onClick={handleNoClick}
            style={noButtonStyle}
          >
            No
          </button>
        </div>
        {noButtonMessage && (
          <div className="message">{noButtonMessage}</div>
        )}
      </div>
    </div>
  );
}

function InvitePage() {
  // Prepopulate a Google Calendar event for Solo
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Solo%27s+Special+Valentine+Date&dates=20250213T170000/20250213T180000&details=Hi+Solo!+Join+me+for+a+memorable+evening+this+Thursday+at+5+pm.+Let%27s+create+beautiful+moments+together.";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div className="page-container">
      {/* Decorative hearts */}
      <div className="decorations">
        <span className="heart" style={{ top: '12%', left: '20%' }}>♥</span>
        <span className="heart" style={{ top: '25%', right: '25%' }}>♥</span>
        <span className="heart" style={{ bottom: '18%', left: '30%' }}>♥</span>
        <span className="heart" style={{ bottom: '12%', right: '20%' }}>♥</span>
      </div>

      <div className="card">
        <h1 className="title">You're Invited, Solo!</h1>
        <p className="description">
          I would be honored if you joined me for a special Valentine’s evening this Thursday at 5 pm.
          Let’s celebrate love with elegance and create beautiful memories together.
        </p>
        <button className="btn accept-btn" onClick={handleAccept}>
          Accept
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/invite" element={<InvitePage />} />
      </Routes>
    </Router>
  );
}

export default App;
