import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

/**
 * Helper function to check if two rectangles overlap.
 */
function rectOverlap(r1, r2) {
  return !(
    r1.left + r1.width < r2.left ||
    r1.left > r2.left + r2.width ||
    r1.top + r1.height < r2.top ||
    r1.top > r2.top + r2.height
  );
}

/**
 * RandomDecorations creates 15 hearts, 5 arrows, and 5 teddy bears.
 * Each decoration is positioned randomly on the screen with a set margin.
 * If two decorations are of the same type, an extra gap is enforced.
 */
const RandomDecorations = () => {
  const [decorations, setDecorations] = useState([]);

  useEffect(() => {
    // Increase margin from edges and between different emojis.
    const margin = 40; // margin from viewport edges
    const extraSpacing = 40; // extra spacing for similar emojis
    const containerWidth = window.innerWidth;
    const containerHeight = window.innerHeight;
    const newDecorations = [];
    // Configuration for each type: hearts, arrows, and teddy bears.
    const config = [
      { type: 'heart', count: 15, size: 32, emoji: 'â™¥' },
      { type: 'arrow', count: 5, size: 32, emoji: 'ðŸ¹' },
      { type: 'teddy', count: 5, size: 48, emoji: 'ðŸ§¸' }
    ];

    config.forEach((item) => {
      for (let i = 0; i < item.count; i++) {
        let attempts = 0;
        let placed = false;
        while (!placed && attempts < 100) {
          attempts++;
          // Generate a random position within the available area (with margin).
          const left =
            Math.random() * (containerWidth - item.size - 2 * margin) + margin;
          const top =
            Math.random() * (containerHeight - item.size - 2 * margin) + margin;
          const newRect = { left, top, width: item.size, height: item.size };

          // Check against all already placed decorations.
          let overlap = false;
          for (let j = 0; j < newDecorations.length; j++) {
            const existing = newDecorations[j];
            // If same type, enforce extraSpacing by enlarging the existing rectangle.
            const requiredExtra = existing.type === item.type ? extraSpacing : 0;
            const existingRect = {
              left: existing.left - requiredExtra,
              top: existing.top - requiredExtra,
              width: existing.size + 2 * requiredExtra,
              height: existing.size + 2 * requiredExtra,
            };
            if (rectOverlap(newRect, existingRect)) {
              overlap = true;
              break;
            }
          }
          if (!overlap) {
            newDecorations.push({
              ...item,
              left,
              top,
              key: `${item.type}-${i}-${attempts}`,
            });
            placed = true;
          }
        }
        // Fallback: if no non-overlapping position is found, place it anyway.
        if (!placed) {
          const left =
            Math.random() * (containerWidth - item.size - 2 * margin) + margin;
          const top =
            Math.random() * (containerHeight - item.size - 2 * margin) + margin;
          newDecorations.push({
            ...item,
            left,
            top,
            key: `${item.type}-${i}-fallback`,
          });
        }
      }
    });
    setDecorations(newDecorations);
  }, []);

  return (
    <>
      {decorations.map((deco) => (
        <span
          key={deco.key}
          className={deco.type}
          style={{
            position: 'absolute',
            left: deco.left,
            top: deco.top,
            fontSize: `${deco.size}px`,
          }}
        >
          {deco.emoji}
        </span>
      ))}
    </>
  );
};

function LandingPage() {
  const navigate = useNavigate();
  const cardRef = useRef(null);
  const [noButtonStyle, setNoButtonStyle] = useState({
    position: 'absolute',
    right: '20px',
    top: '0px',
  });
  const [noButtonMessage, setNoButtonMessage] = useState('');
  const noMessages = [
    "haha you can't catch me ðŸ¤£ðŸ«µ",
    "nope, not this time âŒ",
    "still no! ðŸ˜µ",
    "seriously? ðŸ’”"
  ];

  const handleNoClick = () => {
    const randomIndex = Math.floor(Math.random() * noMessages.length);
    setNoButtonMessage(noMessages[randomIndex]);
    if (cardRef.current) {
      const containerWidth = cardRef.current.offsetWidth;
      const containerHeight = cardRef.current.offsetHeight;
      const buttonWidth = 120;
      const buttonHeight = 45;
      const maxLeft = containerWidth - buttonWidth;
      const maxTop = containerHeight - buttonHeight;
      const newLeft = Math.random() * maxLeft;
      const newTop = Math.random() * maxTop;
      setNoButtonStyle({
        position: 'absolute',
        left: `${newLeft}px`,
        top: `${newTop}px`,
        transition: 'all 0.1s ease-in-out',
      });
    }
  };

  return (
    <div className="page-container">
      {/* Reusable background decorations */}
      <div className="decorations">
        <RandomDecorations />
      </div>

      <div className="card" ref={cardRef}>
        <h1 className="title">Solomiya, will you be my Valentine?</h1>
        <h2 className="subtitle">Don't be "Solo" â€“ let's make it a duo!</h2>
        <div className="button-container">
          <button className="btn yes-btn" onClick={() => navigate('/invite')}>
            Yes ðŸ’–
          </button>
          <button
            className="btn no-btn"
            onClick={handleNoClick}
            style={noButtonStyle}
          >
            No
          </button>
        </div>
        {noButtonMessage && <div className="message">{noButtonMessage}</div>}
      </div>
    </div>
  );
}

function InvitePage() {
  const googleCalendarLink =
    "https://calendar.google.com/calendar/r/eventedit?text=Solo%27s+Special+Valentine+Date&dates=20250213T170000/20250213T180000&details=Hi+Solo!+Join+me+for+a+memorable+evening+this+Thursday+at+5+pm.+Let%27s+create+beautiful+moments+together.";

  const handleAccept = () => {
    window.open(googleCalendarLink, '_blank');
  };

  return (
    <div className="page-container">
      {/* Reusable background decorations */}
      <div className="decorations">
        <RandomDecorations />
      </div>

      <div className="card">
        <h1 className="title">You're Invited, Solo!</h1>
        <p className="description">
          I would be honored if you joined me for a special Valentineâ€™s evening this Thursday at 5 pm.
          Letâ€™s celebrate love with elegance and create beautiful memories together.
        </p>
        <button className="btn accept-btn" onClick={handleAccept}>
          Accept ðŸ’Œ
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <>
      {/* Background song (ensure the audio file is in your public folder) */}
      <audio src="/Lou Val - Eternal Sunshine (Official Visualizer).mp3" autoPlay loop />
      <Router>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/invite" element={<InvitePage />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
